import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import React, { useState, useEffect } from 'react';

import { Container, Row, Col } from 'reactstrap';
import { Nav, NavItem, NavLink } from 'reactstrap';
import {Movie} from '../src/Components/Movie';
import {MovieRest} from '../src/Components/MovieRest';
import {WhisList} from '../src/Components/WhisList';
import { Button, Popover, PopoverHeader,} from 'reactstrap';


var icon = {
  width : '52px',
}

var flex = {
  display: 'flex',
  flexDirection: 'row',
  alignItems: 'center',
}




function App() {

  var nameList = []
  const [movieListAPI, setMovieListAPI] = useState([]);


  useEffect(() => {

    async function loadData(){
      var rawResponse = await fetch('/new-movies');
      var reponse = await rawResponse.json();
      setMovieListAPI(reponse.moviesAPI.results)
      }

     loadData()
    }, []);


    var movieData = movieListAPI.map((list, i) => {
          return list
              })

  movieData.sort(function(a, b) {
          return b.note - a.note;})

  var movieDataFirst = [];
  var movieDataRest = [];

movieDataFirst = movieData.filter((movie, idx) => idx < 3);
movieDataRest = movieData.filter((movie,idx) => idx > 3 )

  const [popoverOpen, setPopoverOpen] = useState(false);
  const toggle = () => setPopoverOpen(!popoverOpen);

  const [favList, setFavList] = useState([]);

     var clickSurLike = async (nomDuFilm, likeMovie, afficheMovie) => {

    if (likeMovie === false) {
      setFavList( [...favList, {name : nomDuFilm, image : afficheMovie}]) 

          await fetch('/wishlist', {
                method: 'POST',
                headers: {'Content-Type':'application/x-www-form-urlencoded'},
                body: 'Nom='+nomDuFilm+'&img='+afficheMovie

          })



    } else if (likeMovie === true) {setFavList( favList.filter((e) => e.name !== nomDuFilm && e.image !== afficheMovie))

      await fetch('/wishlist', {
        method: 'POST',
        headers: {'Content-Type':'application/x-www-form-urlencoded'},
        body: 'Nom='+nomDuFilm

  })
};

              ;}


    var removeWhisList = (nomDuFilm, afficheMovie, etatLike) => {

    setFavList ( favList.filter((e) => e.name !==nomDuFilm))

  }

  var movieListFirst = movieDataFirst.map((oneMovie, i) => {

    if (oneMovie.title.length > 22){

      return(<Movie booleanLike = {oneMovie.like} nivAvis = {oneMovie.Avis} nivMoyenne = {oneMovie.vote_average} nbreVote = {oneMovie.vote_count} nameMovie = {oneMovie.title.slice(0,22)+"..."} affiche = {'https://image.tmdb.org/t/p/w500'+oneMovie.poster_path} resume = {oneMovie.overview.slice(0,350)+"..."} like = {clickSurLike}/>);

    } else {
      return(<Movie booleanLike = {oneMovie.like} nivAvis = {oneMovie.Avis} nivMoyenne = {oneMovie.vote_average} nbreVote = {oneMovie.vote_count} nameMovie = {oneMovie.title} affiche = {'https://image.tmdb.org/t/p/w500'+oneMovie.poster_path} resume = {oneMovie.overview.slice(0,350)+"..."} like = {clickSurLike}/>);
    }

  });


  var movieListRest = movieDataRest.map((oneMovie, i) => {

    if (oneMovie.title.length > 22){
      return(<MovieRest booleanLike = {oneMovie.like} nivAvis = {oneMovie.Avis} nivMoyenne = {oneMovie.vote_average} nbreVote = {oneMovie.vote_count} nameMovie = {oneMovie.title.slice(0,22)+"..."} affiche = {'https://image.tmdb.org/t/p/w500'+oneMovie.poster_path} resume = {oneMovie.overview.slice(0,350)+"..."} like = {clickSurLike}/>);

    } else {
      return(<MovieRest booleanLike = {oneMovie.like} nivAvis = {oneMovie.Avis} nivMoyenne = {oneMovie.vote_average} nbreVote = {oneMovie.vote_count} nameMovie = {oneMovie.title} affiche = {'https://image.tmdb.org/t/p/w500'+oneMovie.poster_path} resume = {oneMovie.overview.slice(0,350)+"..."} like = {clickSurLike}/>);
    }

  });



  var favListComp = favList.map((movieList, i) => {

    return(<WhisList etatLike = {favList} List = {movieList.name} Affiche = {movieList.image} vide= {movieList.name.length}removeWhisList = {removeWhisList}/>)

  })


  var nav = {
    fontSize : '18px',
    fontWeight : 'bold',
    color : 'white'
  }

return (
  <div className ="App">
    <Container>
      <Row className ="header">
        <Col>
        <Nav style={flex}>
        <NavItem>
          <NavLink  >< img src='./pop-corn.png' alt='' style={icon} /></NavLink>
        </NavItem>
        <NavItem>
          <NavLink style={nav} href="#">Connexion</NavLink>
        </NavItem>
        <NavItem>
          <NavLink style={nav} href="#">

          <div>
      <Button id="Popover1" type="button">
        Favoris : {favList.length}
      </Button>
      <Popover placement="bottom" isOpen={popoverOpen} target="Popover1" toggle={toggle}>
        <PopoverHeader>Liste de Favoris</PopoverHeader>
              {favListComp}
      </Popover>
          </div>

          </NavLink>
        </NavItem>
        </Nav>
        </Col>
      </Row>

      <Row className="cards">
          {movieListFirst}
      </Row>
      <Row>
          {movieListRest}
      </Row>

    </Container>
  </div>
  );
};

export default App;
