import '../App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Col } from 'reactstrap';
import {Card, CardImg, CardText, CardBody} from 'reactstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faVideo } from '@fortawesome/free-solid-svg-icons';
import { faHeart } from '@fortawesome/free-solid-svg-icons';
import { faStar } from '@fortawesome/free-solid-svg-icons';
import React, {useState}  from 'react';





function Movie(props) {


  var fiche = {
    marginBottom : '24px'
  };


  var etoile = {
    color: 'yellow'
  }

  var titre = {
    fontSize : '28px',
    fontWeight : 'bold'
  }

  var resume ={
    fontSize : '12px'
  }

  const [likeMovie, setLikeMovie] = useState(false);;
  const [watchMovie, setWatchMovie] = useState(false);;

  var clickSurLike = () => {
    if (likeMovie === false) {
    setLikeMovie(true)} else {setLikeMovie(false)};
    console.log(likeMovie);
  }

  var clickSurCamera = () => {
    if (watchMovie === false) {
    setWatchMovie(true)} else {setWatchMovie(false)};
    console.log(watchMovie);
  }

  var coeur = {color: 'grey'}
  if (likeMovie === true) {
    coeur = {color: '#e74c3c'}
  }

  var camera = {color:'#FBFBFB'}
  if (watchMovie === true) {
    camera = {color: 'black'}
  }



return (

   <Col style={fiche} sm="12" lg="6" xl="4">

      <Card>
        <CardImg top width="100%" src={props.affiche} alt="Card image cap" />
        <CardBody>
          <CardText style={titre}>{props.nameMovie}</CardText>
          <CardText >Like <FontAwesomeIcon onClick={() => clickSurLike()} style={coeur} icon={faHeart} /></CardText>
          <CardText>Nombre de vues {props.nbreVue} <FontAwesomeIcon icon={faVideo} /></CardText>
          <CardText>Mon avis {props.nivAvis} <FontAwesomeIcon style={etoile} icon={faStar} /></CardText>
          <CardText>Moyenne {props.nivMoyenne} <FontAwesomeIcon style={etoile} icon={faStar} /></CardText>
          <CardText style={resume}>{props.resume}</CardText>
        </CardBody>
      </Card>
    </Col>
  );}


export {Movie};
