import React, {useState}  from 'react';

import {PopoverBody } from 'reactstrap';


var list = {
  fontSize : '18px',
  padding : "8px 10px",
}

var image = {
  height : '45px',
  padding: "0px 8px 0px 0px"
}

const [wishList, setWishList] = ([])

function WhisList(props) {

  var Remove = () => {

  props.removeWhisList(props.List, props.Affiche, props.etatLike, props.vide)

  }


  useEffect(() => {

    async function loadData(){
      var rawResponse = await fetch('/wishlist-movie');
      var reponse = await rawResponse.json();
      setWishList(reponse)
      }

     loadData()
    }, []);

return (

  <PopoverBody onClick={() => Remove()} style={list}><img style={image} etatLike = {props.etatLike} src={'https://image.tmdb.org/t/p/w500'+props.Affiche} alt=""></img>{props.List}     </PopoverBody>
        );}



export {WhisList};
