import '../App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Col } from 'reactstrap';
import {Card, CardImg, CardText, CardBody} from 'reactstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faVideo } from '@fortawesome/free-solid-svg-icons';
import { faHeart } from '@fortawesome/free-solid-svg-icons';
import { faStar } from '@fortawesome/free-solid-svg-icons';
import React, {useState}  from 'react';
import { Badge } from 'reactstrap';




function Movie(props) {


  var fiche = {
    marginBottom : '24px'
  }


  var etoile = {
    color: 'yellow'
  }

  var titre = {
    fontSize : '28px',
    fontWeight : 'bold'
  }

  var resume ={
    fontSize : '12px'
  }

  var badge = {
    backgroundColor : 'grey'
  }

  const [likeMovie, setLikeMovie] = useState(false);
  const [watchMovie, setWatchMovie] = useState(false);
  const [watchNumber, setWatchNumber] = useState(0);
  const [myRatingMovie, setMyRatingMovie] = useState(0);
  const [moyenneMovie, setMoyenneMovie] = useState(0);

  var clickSurLike = () => {
    if (likeMovie === false) {
    setLikeMovie(true)} else {setLikeMovie(false)};
    console.log(likeMovie);
  }

  var clickSurCamera = () => {
    if (watchMovie === false) {
    setWatchMovie(true)}
    setWatchNumber(watchNumber+1)
    console.log(watchNumber);
  }

  var clickSurAvisPlus = () => {
    setMyRatingMovie(myRatingMovie+1)
    if (myRatingMovie === 10) {
      setMyRatingMovie(myRatingMovie+0)}
      console.log(myRatingMovie)

  }

  var clickSurAvisMoins = () => {
    setMyRatingMovie(myRatingMovie-1)
    if (myRatingMovie === 0) {
      setMyRatingMovie(myRatingMovie+0)}
      console.log(myRatingMovie)
  }

  var coeur = {color: 'grey'}
  if (likeMovie === true) {
    coeur = {color: '#e74c3c'}
  }

  var camera = {color:'#EAEAEA'}
  if (watchMovie === true) {
    camera = {color: 'black'}
  }

  var tabGlobalRating = []
  for(var i=0;i<10;i++){
      var color = {color: "grey"}
      if(i<myRatingMovie){
          color = {color: '#f1c40f'}
      }
      tabGlobalRating.push(<FontAwesomeIcon style={color} icon={faStar} /> )
  }



return (

   <Col style={fiche} sm="12" lg="6" xl="4">

      <Card>
        <CardImg top width="100%" src={props.affiche} alt="Card image cap" />
        <CardBody>
          <CardText style={titre}>{props.nameMovie}</CardText>
          <CardText >Like <FontAwesomeIcon onClick={() => clickSurLike()} style={coeur} icon={faHeart} /></CardText>
          <CardText>Nombre de vues {props.nbreVue} <FontAwesomeIcon onClick={() => clickSurCamera()} style={camera} icon={faVideo} />    <Badge style= {badge}>{watchNumber}</Badge></CardText>
          <CardText>Mon avis{tabGlobalRating} <Badge onClick={() => clickSurAvisMoins()} style= {badge}>-1</Badge>   <Badge onClick={() => clickSurAvisPlus()} style= {badge}>+1</Badge></CardText>
          <CardText>nivMoyenne<FontAwesomeIcon style={etoile} icon={faStar} /> {props.nbreVote} </CardText>
          <CardText style={resume}>{props.resume}</CardText>
        </CardBody>
      </Card>
    </Col>
  );}


export {Movie};
