import '../App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Col } from 'reactstrap';
import {Card, CardImg, CardText, CardBody} from 'reactstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faVideo } from '@fortawesome/free-solid-svg-icons';
import { faHeart } from '@fortawesome/free-solid-svg-icons';
import { faStar } from '@fortawesome/free-solid-svg-icons';
import React, {useState}  from 'react';
import { Badge } from 'reactstrap';




function Movie(props) {

  var cardText = {
    paddingTop : '3px',

  }



  var fiche = {
    marginBottom : '24px'
  }


  var etoile = {
    color: 'yellow'
  }

  var titre = {
    color : 'black',
    fontSize : '28px',
    fontWeight : 'bold'
  }

  var badge = {
    backgroundColor : 'grey'
  }

  var Like = {
    backgroundColor : "white",
    borderWidth : "3px",
    borderColor : "white"
  }
  var sousVotePour ='{grey'
  var votePour = {color : '#FEBE00'}



  const [likeMovie, setLikeMovie] = useState(false);
  const [watchMovie, setWatchMovie] = useState(false);
  const [watchNumber, setWatchNumber] = useState(0);
  const [myRatingMovie, setMyRatingMovie] = useState(0);
  const [myVote, setMyVote] = useState(false);



  var clickSurLike = () => {
    if (likeMovie === false) {
        setLikeMovie(true)
          } else {
        setLikeMovie(false)};
  }

      if (likeMovie === true)  Like = {
              backgroundColor : "#FEBE00",
              borderWidth : "3px",
              borderColor : "#B49E24"
                    }

      if (likeMovie ===true) votePour = {
              color : '#00086D'
      }

      if (likeMovie===true) sousVotePour = {
              color : "white"
      }
  var clickSurCamera = () => {
    if (watchMovie === false) {
    setWatchMovie(true)}
    setWatchNumber(watchNumber+1)

  }

  var clickSurAvisPlus = () => {


    console.log("youhou", myVote)

    setMyRatingMovie(myRatingMovie+1)
    if (myRatingMovie === 10) {
      setMyRatingMovie(myRatingMovie+0)}

  }

  var clickSurAvisMoins = () => {
    setMyRatingMovie(myRatingMovie-1)
    if (myRatingMovie === 0) {
      setMyRatingMovie(myRatingMovie+0)}

  }

  var clickStar = (compteur) => {

    setMyRatingMovie(compteur);
    setMyVote(true)

  }



  var newMoyenne = ((props.nivMoyenne * props.nbreVote)+ (myRatingMovie)) / (props.nbreVote+1);

  var nbreVote = props.nbreVote;
    if(myVote === true) {
      nbreVote = props.nbreVote +1
    }

    var coeur = {color: 'grey'}
  if (likeMovie === true) {
    coeur = {color: '#e74c3c'}
  }

  var camera = {color:'#EAEAEA'}
  if (watchMovie === true) {
    camera = {color: 'black'}
  }

  var tabMyRating = []
  for(var i=0; i<10; i++){
      var color = {color: "grey"}
      if(i<myRatingMovie){
          color = votePour
      }
      let compteur = i+1
    tabMyRating.push(<FontAwesomeIcon className = "pointer" onClick={() => clickStar(compteur)} style={color} icon={faStar} /> )
  }

  var tabGlobalRating = []
  for(var i=0;i<10;i++){
      var color = {color: "grey"}
      if(i<newMoyenne){
          color = votePour
      }

      tabGlobalRating.push(<FontAwesomeIcon className = "pointer" style={color} icon={faStar} /> )
  }



return (

   <Col style={fiche} sm="12" lg="6" xl="4">

      <Card style={Like}>
        {/* <CardImg top width="100%" src={props.affiche} alt="Card image cap" /> */}
        <div className="position-relative">
        <CardImg top width="100%" src={props.affiche} className="noirci"/> 
        <h2 className="resume">{props.resume}</h2>
        </div>

        <CardBody style={cardText}>
          <CardText style={titre}>{props.nameMovie}</CardText>
          <CardText >Like <FontAwesomeIcon className = "pointer" onClick={() => clickSurLike()} style={coeur} icon={faHeart} /></CardText>
          <CardText>Nombre de vues {props.nbreVue} <FontAwesomeIcon className = "pointer" onClick={() => clickSurCamera()} style={camera} icon={faVideo} />    <Badge style= {badge}>{watchNumber}</Badge></CardText>
          <CardText>Mon avis{tabMyRating} {/* <Badge onClick={() => clickSurAvisMoins()} style= {badge}>-1</Badge>   <Badge onClick={() => clickSurAvisPlus()} style= {badge}>+1</Badge> */}</CardText>
          <CardText>Moyenne {tabGlobalRating} ({nbreVote} avis) </CardText>
          <CardText></CardText>
        </CardBody>
      </Card>
    </Col>
  );}


export {Movie};
