import '../App.css';
import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Col } from 'reactstrap';
import {
  Card, CardImg, CardText, CardBody} from 'reactstrap';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faVideo } from '@fortawesome/free-solid-svg-icons';
import { faHeart } from '@fortawesome/free-solid-svg-icons';
import { faStar } from '@fortawesome/free-solid-svg-icons';




function Movie(props) {

  var fiche = {
    marginBottom : '10px'  };

return (

   <Col style={fiche} sm="12" lg="6" xl="4">

      <Card>
        <CardImg top width="100%" src={props.affiche} alt="Card image cap" />
        <CardBody>
          <CardText>Like <FontAwesomeIcon icon={faHeart} /></CardText>
          <CardText>Nombre de vues <FontAwesomeIcon icon={faVideo} /></CardText>
          <CardText>Mon avis {props.nivAvis} <FontAwesomeIcon icon={faStar} /></CardText>
          <CardText>Moyenne {props.nivMoyenne} <FontAwesomeIcon icon={faStar} /></CardText>
          <CardText>{props.nameMovie}</CardText>
          </CardBody>
      </Card>
    </Col>



);}


export {Movie};
