import '../App.css';
import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Col } from 'reactstrap';
import {
  Card, CardImg, CardText, CardBody} from 'reactstrap';

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faVideo } from '@fortawesome/free-solid-svg-icons';
import { faHeart } from '@fortawesome/free-solid-svg-icons';
import { faStar } from '@fortawesome/free-solid-svg-icons';




function Movie(props) {

var movieData = [  {like: true,
                        Vues: 7,
                        Avis: 3,
                        Moyenne: 6,
                        Nom: "BadBoy",
                        img: badboy,
                        sum : "Si Mike Lowrey est un séducteur invétéré, héritier d'une fortune et policier par passion, son collègue et ami Marcus Burnett est un homme rangé, marié et père de famille. Leur amitié ne les empêche cependant pas d'avoir des méthodes parfaitement différentes."},
                        { like: false,
                          Vues: 7,
                          Avis: 3,
                          Moyenne: 6,
                          Nom: "Jumanji",
                          img: jumanji,
                          sum : "Lors d'une partie de Jumanji, un jeu très ancien, le jeune Alan est propulsé sous les yeux de son amie d'enfance, Sarah, dans un étrange pays. Il ne pourra s'en échapper que lorsqu'un autre joueur reprendra la partie et le libérera sur un coup de dés."},
                          { like: false,
                            Vues: 7,
                            Avis: 3,
                            Moyenne: 6,
                            Nom: "Frozen",
                            img: frozen,
                            sum : "Elsa et Anna sont les deux princesses du royaume d'Arendelle. Elsa, l'aînée, possède un puissant pouvoir : celui de contrôler la neige et la glace. Toutefois, son pouvoir n'est connu que de ses parents et de sa sur car Elsa serait immédiatement [...]"},
                            { like: true,
                              Vues: 7,
                              Avis: 3,
                              Moyenne: 6,
                              Nom: "Star Wars",
                              img: starwars,
                              sum : "Il y a bien longtemps, dans une galaxie très lointaine. La guerre civile fait rage entre l'Empire galactique et l'Alliance rebelle. Capturée par les troupes de choc de l'Empereur menées par le sombre et impitoyable Dark Vador, la princesse [...]"},
                              { like: true,
                                Vues: 7,
                                Avis: 3,
                                Moyenne: 6,
                                Nom: "Malefique",
                                img: maleficent,
                                sum : "Poussée par la vengeance et une volonté farouche de protéger les terres qu'elle préside, Maléfique place cruellement une malédiction irrévocable sur la Princesse Aurore, l'enfant du roi Stéphane et de la reine Oriane. À l'aube de ses 16 ans,[...]"},];

  var fiche = {
    marginBottom : '24px'
  };

  var coeur = {
    color: 'red'
  }
  var etoile = {
    color: 'yellow'
  }

  var titre = {
    fontSize : '28px',
    fontWeight : 'bold'
  }

  var resume ={
    fontSize : '12px'
  }

return (

   <Col style={fiche} sm="12" lg="6" xl="4">

      <Card>
        <CardImg top width="100%" src={props.affiche} alt="Card image cap" />
        <CardBody>
          <CardText style={titre}>{props.nameMovie}</CardText>
          <CardText >Like <FontAwesomeIcon style={coeur} icon={faHeart} /></CardText>
          <CardText>Nombre de vues {props.nbreVue} <FontAwesomeIcon icon={faVideo} /></CardText>
          <CardText>Mon avis {props.nivAvis} <FontAwesomeIcon style={etoile} icon={faStar} /></CardText>
          <CardText>Moyenne {props.nivMoyenne} <FontAwesomeIcon style={etoile} icon={faStar} /></CardText>
          <CardText style={resume}>{props.resume}</CardText>
        </CardBody>
      </Card>
    </Col>
  );}


export {Movie};
