import '../App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Col } from 'reactstrap';
import {Card, CardImg, CardText, CardBody} from 'reactstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faVideo } from '@fortawesome/free-solid-svg-icons';
import { faHeart } from '@fortawesome/free-solid-svg-icons';
import { faStar } from '@fortawesome/free-solid-svg-icons';
import React, {useState}  from 'react';
import { Badge } from 'reactstrap';




function Movie(props) {


  var fiche = {
    marginBottom : '24px'
  }


  var etoile = {
    color: 'yellow'
  }

  var titre = {
    fontSize : '28px',
    fontWeight : 'bold'
  }

  var resume ={
    fontSize : '12px'
  }

  var badge = {
    backgroundColor : 'grey'
  }

  var backgroundLike = {
    backgroundColor : "white"
  }

  const [likeMovie, setLikeMovie] = useState(false);
  const [watchMovie, setWatchMovie] = useState(false);
  const [watchNumber, setWatchNumber] = useState(0);
  const [myRatingMovie, setMyRatingMovie] = useState(0);
  const [moyenneMovie, setMoyenneMovie] = useState(props.nivMoyenne);
  const [myVote, setMyVote] = useState(false)



  var clickSurLike = () => {
    if (likeMovie === false) {
    setLikeMovie(true)
      backgroundLike = {
      backgroundColor : "red"
    }} else {setLikeMovie(false)
        backgroundLike = {
        backgroundColor : "white"
      }};

  }

  var clickSurCamera = () => {
    if (watchMovie === false) {
    setWatchMovie(true)}
    setWatchNumber(watchNumber+1)

  }

  var clickSurAvisPlus = () => {


    console.log("youhou", myVote)

    setMyRatingMovie(myRatingMovie+1)
    if (myRatingMovie === 10) {
      setMyRatingMovie(myRatingMovie+0)}

  }

  var clickSurAvisMoins = () => {
    setMyRatingMovie(myRatingMovie-1)
    if (myRatingMovie === 0) {
      setMyRatingMovie(myRatingMovie+0)}

  }

  var clickStar = (compteur) => {

    setMyRatingMovie(compteur);
    setMyVote(true)

  }

  var newMoyenne = ((props.nivMoyenne * props.nbreVote)+ (myRatingMovie)) / (props.nbreVote+1);

  var nbreVote = props.nbreVote;
    if(myVote === true) {
      nbreVote = props.nbreVote +1
    }

    var coeur = {color: 'grey'}
  if (likeMovie === true) {
    coeur = {color: '#e74c3c'}
  }

  var camera = {color:'#EAEAEA'}
  if (watchMovie === true) {
    camera = {color: 'black'}
  }

  var tabMyRating = []
  for(var i=0; i<10; i++){
      var color = {color: "grey"}
      if(i<myRatingMovie){
          color = {color: '#f1c40f'}
      }
      let compteur = i+1
    tabMyRating.push(<FontAwesomeIcon onClick={() => clickStar(compteur)} style={color} icon={faStar} /> )
  }

  var tabGlobalRating = []
  for(var i=0;i<10;i++){
      var color = {color: "grey"}
      if(i<newMoyenne){
          color = {color: '#f1c40f'}
      }
      tabGlobalRating.push(<FontAwesomeIcon style={color} icon={faStar} /> )
  }



return (

   <Col style={fiche} sm="12" lg="6" xl="4">

      <Card style={backgroundLike}>
        <CardImg top width="100%" src={props.affiche} alt="Card image cap" />
        <CardBody>
          <CardText style={titre}>{props.nameMovie}</CardText>
          <CardText >Like <FontAwesomeIcon onClick={() => clickSurLike()} style={coeur} icon={faHeart} /></CardText>
          <CardText>Nombre de vues {props.nbreVue} <FontAwesomeIcon onClick={() => clickSurCamera()} style={camera} icon={faVideo} />    <Badge style= {badge}>{watchNumber}</Badge></CardText>
          <CardText>Mon avis{tabMyRating} {/* <Badge onClick={() => clickSurAvisMoins()} style= {badge}>-1</Badge>   <Badge onClick={() => clickSurAvisPlus()} style= {badge}>+1</Badge> */}</CardText>
          <CardText>nivMoyenne {tabGlobalRating} {nbreVote} </CardText>
          <CardText style={resume}>{props.resume}</CardText>
        </CardBody>
      </Card>
    </Col>
  );}


export {Movie};
